mapper.select:
SELECT cas.case_no, cas.case_save_date, cas.approval_no, bas.approval_name, msc.min_sj_sgn_category_name, mcc.min_sj_court_category_name, bdc.confirmed_url FROM Barom_case cas, Barom_CaseBasicInfo bci, BR_Min_Sj_Sgn_Category msc, BR_Min_Sj_Court_Category mcc, Barom_approval_status bas, Barom_DocumentConfirmed bdc WHERE cas.status_no = 2 AND cas.case_no = bci.case_no AND cas.case_no = bdc.case_no AND cas.approval_no = bas.approval_no AND bci.min_sj_sgn_category_no = msc.min_sj_sgn_category_no AND bci.min_sj_court_category_no = mcc.min_sj_court_category_no
AND cas.approval_no = #{docOption}
AND bci.min_sj_court_category_no = #{courtOption}
AND cas.case_no = #{caseNo}
AND cas.case_save_date BETWEEN #{minDate} AND #{maxDate}
ORDER BY cas.case_save_date DESC

mapper.update:
UPDATE Barom_case

SET approval_no = '2'
WHERE case_no = #{case_no}

UPDATE Barom_case

SET approval_no = '3', case_refuse_reason = #{case_refuse_reason}
WHERE case_no = #{case_no}

mapper.select:
SELECT t6.*, bt.TRIAL_RESULT_STATUS FROM (SELECT t5.CASE_CONFIRMED_NO, t5.case_confirmed_date, t5.caseconfirmed_status_no, t5.MIN_SJ_SGN_CATEGORY_NAME, t5.document_url, t5.CASECONFIRMED_STATUS_NAME, bdc.CONFIRMED_URL FROM Barom_DocumentConfirmed bdc, (SELECT t4.CASE_CONFIRMED_NO, t4.case_confirmed_date, t4.caseconfirmed_status_no, t4.MIN_SJ_SGN_CATEGORY_NAME, t4.document_url, t4.case_no, cs.CASECONFIRMED_STATUS_NAME FROM BR_Case_confirmed_Status cs, (SELECT t3.CASE_CONFIRMED_NO, t3.case_no, t3.caseconfirmed_status_no, t3.case_confirmed_date, t3.MIN_SJ_SGN_CATEGORY_NAME, bdd.document_url FROM Barom_Defense_Document bdd, (SELECT t2.*, bd.DEFENSE_NO FROM (SELECT t1.CASE_CONFIRMED_NO, t1.caseconfirmed_status_no, t1.case_confirmed_date, t1.case_no, sc.MIN_SJ_SGN_CATEGORY_NAME FROM BR_Min_Sj_Sgn_Category sc, (SELECT Case_Confirmed_no, min_sj_sgn_category_no, caseconfirmed_status_no, case_confirmed_date, case_no FROM Barom_Case_Confirmed
WHERE min_sj_court_category_no != 0
AND caseconfirmed_status_no = #{statusOption}
AND case_confirmed_date BETWEEN #{minDate} AND #{maxDate}
AND Case_Confirmed_no = #{confirmedNo}
ORDER BY case_confirmed_date DESC ) t1 WHERE sc.min_sj_sgn_category_no = t1.MIN_SJ_SGN_CATEGORY_NO) t2 LEFT OUTER JOIN Barom_Defense bd ON bd.CASE_CONFIRMED_NO = t2.CASE_CONFIRMED_NO) t3 WHERE bdd.Defense_no(+) = t3.Defense_no) t4 WHERE cs.CASECONFIRMED_STATUS_NO = t4.caseconfirmed_status_no) t5 WHERE t5.case_no = bdc.CASE_NO) t6 LEFT OUTER JOIN Barom_trial bt ON t6.Case_Confirmed_no = bt.CASE_CONFIRMED_NO
WHERE bt.TRIAL_RESULT_STATUS IS NULL
WHERE bt.TRIAL_RESULT_STATUS = '판결'
